package com.example.amine_mbarek_lsi3_mesure_glycemie.vue;import static com.example.amine_mbarek_lsi3_mesure_glycemie.R.id.btn;import androidx.appcompat.app.AlertDialog;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.RadioButton;import android.widget.SeekBar;import android.widget.TextView;import android.widget.Toast;import com.example.amine_mbarek_lsi3_mesure_glycemie.R;public class MainActivity extends AppCompatActivity {    private EditText value;    private RadioButton rbtOui;    private RadioButton rbtNon;    private SeekBar seekBar;    private Button btn;    private TextView txt;    private TextView txtshow;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        init();    }    private void init() {        value = findViewById(R.id.value);        rbtOui = findViewById(R.id.rbtOui);        rbtNon = findViewById(R.id.rbtNon);        seekBar = findViewById(R.id.seekbar);        btn = findViewById(R.id.btn);        txt = findViewById(R.id.tvage);        txtshow = findViewById(R.id.text3);        seekBar.setOnSeekBarChangeListener(new SeekBar.OnSeekBarChangeListener() {            public void onProgressChanged(SeekBar seekBar, int progress, boolean fromUser) {                Log.i("Information", "onProgressChanged " + progress);                txt.setText("Votre âge : "+ progress);            }            public void onStartTrackingTouch(SeekBar seekBar) {            }            public void onStopTrackingTouch(SeekBar seekBar) {            }        });        btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v)            {                int age= seekBar.getProgress();                System.out.println("age ="+age);                String valmesurestr=(value.getText().toString());                if (age == 0 && valmesurestr.isEmpty()){                    Toast.makeText(getApplicationContext(), "Please enter a valid age and value", Toast.LENGTH_SHORT).show();                }else if (age == 0) {                    Toast.makeText(getApplicationContext(), "Please enter a valid age", Toast.LENGTH_SHORT).show();                } else if (valmesurestr.isEmpty()) {                    Toast.makeText(getApplicationContext(), "Please enter a valid value", Toast.LENGTH_SHORT).show();                } else {                    float valmesure = Float.parseFloat(valmesurestr);                    System.out.println("val ="+valmesure);                    boolean isFasting=rbtOui.isChecked();                    System.out.println("isFasting? ="+ isFasting);                    if (isFasting) {                        if (age > 13) {                            if (valmesure >= 5.0 && valmesure <= 7.2) {                                System.out.println("Niveau de glycémie est normale");                                txtshow.setText("Niveau de glycémie est normale");                            } else if (valmesure < 5.0) {                                System.out.println("Niveau de glycémie est trop bas");                                txtshow.setText("Niveau de glycémie est trop bas");                            } else if (valmesure > 7.2) {                                System.out.println("Niveau de glycémie est trop élevée");                                txtshow.setText("Niveau de glycémie est trop élevée");                            }                        } else if (age >= 6 && age <= 12) {                            if (valmesure >= 5.0 && valmesure <= 10.0) {                                System.out.println("Niveau de glycémie est normale");                                txtshow.setText("Niveau de glycémie est normale");                            } else {                                System.out.println("Niveau de glycémie est trop bas");                                txtshow.setText("Niveau de glycémie est trop bas");                            }                        } else if (age < 6) {                            if (valmesure >= 5.5 && valmesure <= 10.0) {                                System.out.println("Niveau de glycémie est normale");                                txtshow.setText("Niveau de glycémie est normale");                            } else {                                System.out.println("Niveau de glycémie est trop bas");                                txtshow.setText("Niveau de glycémie est trop bas");                            }                        }                    } else {                        if (valmesure < 10.5) {                            System.out.println("Niveau de glycémie est normale");                            txtshow.setText("Niveau de glycémie est normale");                        } else {                            System.out.println("Niveau de glycémie est trop élevée");                            txtshow.setText("Niveau de glycémie est trop élevée");                        }                    }                }            }        });    }}